mapper.select:
select broadcast.* , broadcast_kinds.broadcast_kindsname,members.realname as membersrealname from broadcast left join members on members.membersid=broadcast.membersid left join broadcast_kinds on broadcast.broadcast_kindsid = broadcast_kinds.broadcast_kindsid where 1=1
and broadcast.broadcast_kindsid =#{broadcast_kindsid}
and ( broadcast.title like CONCAT('%',#{keyword},'%')
)
and broadcast.membersid =#{membersid}
order by broadcastid desc
limit #{start},#{limit}

select count(1) from broadcast left join members on members.membersid=broadcast.membersid left join broadcast_kinds on broadcast.broadcast_kindsid = broadcast_kinds.broadcast_kindsid where broadcast.broadcast_kindsid = broadcast_kinds.broadcast_kindsid
and broadcast.broadcast_kindsid =#{broadcast_kindsid}
and ( broadcast.title like CONCAT('%',#{keyword},'%')
)
and broadcast.membersid =#{membersid}

select a.* , b.broadcast_kindsname from broadcast a , broadcast_kinds b where a.broadcast_kindsid =
b.broadcast_kindsid

    and a.title = #{title}


    and a.image = #{image}


    and a.broadcast_kindsid = #{broadcast_kindsid}


    and a.istop = #{istop}


    and a.isflv = #{isflv}


    and a.contents = #{contents}


    and a.addtime = #{addtime}


    and a.hits = #{hits}
