 where membersid=#{membersid}

mapper.select:
select * from members where 1=1
and ( members.username like CONCAT('%',#{keyword},'%') or members.realname like CONCAT('%',#{keyword},'%')
)
order by membersid desc
limit #{start},#{limit}

select count(1) from members where 1=1
and ( members.username like CONCAT('%',#{keyword},'%') or members.realname like CONCAT('%',#{keyword},'%')
)

select a.* from members a where 1=1

    and a.username = #{username}


    and a.password = #{password}


    and a.realname = #{realname}


    and a.sex = #{sex}


    and a.birthday = #{birthday}


    and a.contact = #{contact}


    and a.image = #{image}


    and a.regdate = #{regdate}
